import pandas as pd import os path = os.getcwd()os.chdir(path)df = pd.read_csv('corona_jeju.csv', encoding='cp949')# 1. 데이터프레임의 특정 열만 추출하고 싶을 때 df1 = df[['제주 확진자', '자가격리중']]# 2. 데이터프레임 컬럼명 변경df1 = df1.rename(columns = {'제주 확진자' : '확진자'})# 3. iloc: integer position로 값 탐색for i in range(len(df)):     num = df.iloc[i]['제주 확진자'] # i번째 행의 '제주 확진자' 컬럼의 값 추출    print(num)# 4. loc: label로 값 탐색num = df.loc[:, ['제주 확진자']]print(num)    # 5. 특정 값 탐색date = df['기준일자'][df['자가격리(확진자 접촉)'] == 1].values[0]# df의 자가격리(확진자 접촉) 컬럼의 값이 1인 행의 '기준일자'값을 반환 print(date)# 6. itterow() for i, row in df.iterrows():    val = row['기준일자']    print(val)            